        const canvas = this.$refs[`captcha_${this._uid}`];
        const ctx = canvas.getContext('2d');
// console.log('canvas ID= '+canvas);
        // Clear the canvas
        ctx.clearRect(0, 0, canvas.width, canvas.height);

